package eu.europa.esig.dss.asic.cades.extension.asice;

import eu.europa.esig.dss.alert.SilentOnStatusAlert;
import eu.europa.esig.dss.asic.cades.ASiCWithCAdESSignatureParameters;
import eu.europa.esig.dss.asic.cades.signature.ASiCWithCAdESService;
import eu.europa.esig.dss.asic.cades.validation.AbstractASiCWithCAdESTestValidation;
import eu.europa.esig.dss.diagnostic.CertificateWrapper;
import eu.europa.esig.dss.diagnostic.DiagnosticData;
import eu.europa.esig.dss.diagnostic.SignatureWrapper;
import eu.europa.esig.dss.enumerations.SignatureLevel;
import eu.europa.esig.dss.model.DSSDocument;
import eu.europa.esig.dss.model.InMemoryDocument;
import eu.europa.esig.dss.spi.DSSUtils;
import eu.europa.esig.dss.spi.x509.CertificateSource;
import eu.europa.esig.dss.spi.x509.CommonTrustedCertificateSource;
import eu.europa.esig.dss.validation.CertificateVerifier;
import eu.europa.esig.dss.validation.SignedDocumentValidator;
import org.junit.jupiter.api.BeforeEach;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class DSS2361Test extends AbstractASiCWithCAdESTestValidation {

    private static CertificateVerifier certificateVerifier;

    @BeforeEach
    public void init() {
        certificateVerifier = getCompleteCertificateVerifier();
        certificateVerifier.addTrustedCertSources(getTrustedCertSource());
        certificateVerifier.setAlertOnExpiredSignature(new SilentOnStatusAlert());
        certificateVerifier.setAlertOnMissingRevocationData(new SilentOnStatusAlert());
    }

    private CertificateSource getTrustedCertSource() {
        CertificateSource trustedCertSource = new CommonTrustedCertificateSource();
        trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(""));
        trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(""));
        trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(""));
        trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(""));
        trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(""));
        return trustedCertSource;
    }

    @Override
    protected DSSDocument getSignedDocument() {
        DSSDocument dssDocument = new InMemoryDocument(getClass().getResourceAsStream("/validation/ASiC-E-CAdES-BpLTA.sce"));

        ASiCWithCAdESService service = new ASiCWithCAdESService(certificateVerifier);
        service.setTspSource(getGoodTsa());

        ASiCWithCAdESSignatureParameters parameters = new ASiCWithCAdESSignatureParameters();
        parameters.setSignatureLevel(SignatureLevel.CAdES_BASELINE_LTA);
        DSSDocument extendedDocument = service.extendDocument(dssDocument, parameters);

        return extendedDocument;
    }

    @Override
    protected SignedDocumentValidator getValidator(DSSDocument signedDocument) {
        SignedDocumentValidator validator = super.getValidator(signedDocument);
        CertificateVerifier offlineCertificateVerifier = getOfflineCertificateVerifier();
        offlineCertificateVerifier.addTrustedCertSources(getTrustedCertSource());
        validator.setCertificateVerifier(offlineCertificateVerifier);
        return validator;
    }

    @Override
    protected void checkRevocationData(DiagnosticData diagnosticData) {
        super.checkRevocationData(diagnosticData);

        SignatureWrapper signature = diagnosticData.getSignatureById(diagnosticData.getFirstSignatureId());
        CertificateWrapper signingCertificate = signature.getSigningCertificate();
        // no new revocation data is being added
        assertEquals(2, signingCertificate.getCertificateRevocationData().size());
    }

}
